import React from 'react';
import { useState } from 'react';
import { useQuery, useMutation } from '@apollo/react-hooks';
import { QUERY_PRODUCT_PRICE, MUTATION_PRICE } from '../graphql';
import { addNotification } from '../utils/toast';
import { submitPrice } from '../utils/price';
import ToastContainer from '../components/ToastContainer';
import { Card, CardBody, CardTitle } from '../components/Card';
import { FormGroup, InputPrice } from '../components/Form';
import { Button } from '../components/Button';
import { Growing } from '../components/Spinner';
import { Alert } from '../components/Alerts';
import { useApolloClient } from '@apollo/react-hooks';
import gql from 'graphql-tag';

export interface DashboardState {
  price: number;
}

interface ProductInventoryData {
  getProduct: ProductInventory;
}

interface ProductInventory {
  price: number;
}

const Dashboard: React.FC = () => {
  const [notifications, updateNotifications] = useState([]);

  // Querys
  const { loading, error, data } = useQuery<ProductInventoryData>(
    QUERY_PRODUCT_PRICE,
    {
      onError: (error) => {
        console.log(error);
      },
    }
  );

  // Mutations
  const [
    updatePrice,
    { loading: MutationUpdatePriceLoading, error: MutationUpdatePriceError },
  ] = useMutation(MUTATION_PRICE, {
    onError: (error) => {
      console.log(error);
    },
    onCompleted: () => {
      addNotification(
        'Notification',
        `Your price has been updated to $${localData.getProduct.price.toFixed(
          2
        )}`,
        'info',
        notifications,
        updateNotifications
      );
    },
  });

  // Local Methods
  const removeNotification = (id: number) => {
    const data = notifications;
    const toasts = data.filter((item): any => item.id !== id);

    updateNotifications(toasts);
  };

  const client = useApolloClient();

  const onChangeMe = (e) => {
    const price = parseFloat(e.target.value);

    client.writeData({
      data: {
        getProduct: {
          id: '123',
          price,
          __typename: 'Product',
        },
      },
    });
  };

  if (loading) {
    return <Growing visible={true} fullScreen={true} />;
  }

  if (error)
    return (
      <div className="container">
        <Alert type="danger">Network Error.</Alert>
      </div>
    );

  const QUERY_PRODUCT = gql`
    query {
      getProduct @client {
        id
        price
      }
    }
  `;

  const { data: localData } = useQuery(QUERY_PRODUCT);

  return (
    <div className="container">
      {console.log(localData)}
      <ToastContainer
        allToasts={notifications}
        removeNotification={removeNotification}
      />
      <div className="row align-items-center">
        <div className="p-3 mb-3 col-3">
          <Card>
            <CardBody>
              <CardTitle>
                Item Price: {data.getProduct.price.toFixed(2)}
              </CardTitle>
              <form onSubmit={() => event.preventDefault()}>
                <FormGroup>
                  <InputPrice
                    onChange={onChangeMe}
                    labelTitle="Item Price"
                    price={localData.getProduct.price.toFixed(2)}
                  />
                  <Button
                    submitPrice={submitPrice}
                    updatePrice={updatePrice}
                    query={QUERY_PRODUCT_PRICE}
                    mutateLoading={MutationUpdatePriceLoading}
                    price={localData.getProduct.price}
                  >
                    <Growing visible={MutationUpdatePriceLoading} />
                    {MutationUpdatePriceLoading ? ' Loading...' : 'Update'}
                    {MutationUpdatePriceError ? 'Network error occured.' : ''}
                  </Button>
                </FormGroup>
              </form>
            </CardBody>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
