import { useState, useEffect } from 'react';
import { subtractCount } from '../utils/counter';
import { PayPalButton } from 'react-paypal-button-v2';

const HomePage = () => {
  const [height, updateHeight]: any = useState(0);
  const [count, updateCount]: any = useState(1);

  useEffect(() => {
    console.log(count);
    updateHeight(window.innerHeight);
  });

  return (
    <div className="container">
      <div className="row align-items-center">
        <div className="col-sm order-2 order-md-1">
          <h1>Purell Santizer</h1>
          <p>
            Keep your loved ones clean. <br /> In stock and ready to ship.
          </p>
          <ul
            className="list-group list-group-horizontal"
            style={{ marginBottom: '2rem' }}
          >
            <li className="list-group-item">Per Item: $7</li>
            <li className="list-group-item">Shipping: $4.25</li>
            <li className="list-group-item">Total: ${count * 7 + 4.25}</li>
          </ul>
          <PayPalButton
            style={{
              color: 'blue',
              shape: 'pill',
              label: 'pay',
              height: 50
            }}
            // shippingPreference="NO_SHIPPING" // default is "GET_FROM_FILE"
            createOrder={(data, actions) => {
              return actions.order.create({
                purchase_units: [
                  {
                    amount: {
                      currency_code: 'USD',
                      value: count * 7
                    }
                  }
                ]
                // application_context: {
                //   shipping_preference: "NO_SHIPPING" // default is "GET_FROM_FILE"
                // }
              });
            }}
            onSuccess={(details, data) => {
              alert(
                'Transaction completed by ' + details.payer.name.given_name
              );

              // OPTIONAL: Call your server to save the transaction
              return fetch('/paypal-transaction-complete', {
                method: 'post',
                body: JSON.stringify({
                  orderID: data.orderID
                })
              });
            }}
          />
        </div>
        <div className="col-sm order-1 order-md-2">
          <div className="image_container">
            <img src="purell-sanitizer-02.jpg" alt="purell-sanitizer" />
          </div>
          <div className="counter_container">
            <button
              type="button"
              className="btn btn-primary"
              onClick={() => subtractCount(count, updateCount)}
            >
              -
            </button>
            <input
              value={count}
              type="number"
              disabled
              onChange={event => updateCount(parseInt(event.target.value))}
            />
            <button
              type="button"
              className="btn btn-primary"
              onClick={() => updateCount(count + 1)}
            >
              +
            </button>
          </div>
        </div>
      </div>
      <style jsx>{`
        .container,
        .row {
          height: ${height}px;
        }
        h1 {
          font-size: 48px;
          font-weight: 500;
          text-align: center;
          line-height: 0.8;
        }
        p {
          font-size: 20px;
          font-weight: 400;
          text-align: center;
          margin-bottom: 2rem;
        }
        .button_container {
          text-align: center;
        }
        .button_container button {
          width: 322px;
          font-size: 24px;
          text-transform: uppercase;
          padding: 1rem 0.75rem;
          border-radius: 50px;
        }

        .image_container {
          text-align: center;
        }

        .image_container img {
          max-width: 345px;
          width: 100%;
          margin-bottom: 2rem;
        }

        .counter_container {
          text-align: center;
        }

        .counter_container button {
          position: relative;
          z-index: 2;
          width: 38px;
        }

        .counter_container input {
          font-size: 20px;
          text-align: center;
          padding: 0;
          border: none;
        }

        @media (min-width: 768px) {
          h1 {
            font-size: 150px;
            text-align: left;
          }
          p {
            font-size: 34px;
            text-align: left;
          }
          .image_container img {
            max-width: 100%;
          }
          .counter_container {
            text-align: center;
            margin-bottom: 2rem;
          }
          .button_container {
            text-align: left;
          }
          .button_container button {
            font-size: 27px;
            padding: 1.7rem 0.75rem;
          }
        }
      `}</style>
    </div>
  );
};

export default HomePage;
