import React from 'react';
import { v4 as uuid } from 'uuid';
import { Tween } from 'react-gsap';
import { useState } from 'react';
import { useQuery, useMutation } from '@apollo/react-hooks';
import { QUERY_PRODUCT_PRICE, MUTATION_PRICE } from '../graphql';

export interface DashboardState {
  price: number;
  toggleToast?: number;
}

const Dashboard: React.FC = () => {
  const [state, updateState] = useState<DashboardState>({
    price: (7.0 * 100 + 0.0 * 100) / 100,
    toggleToast: 0
  });

  const { loading, error, data } = useQuery(QUERY_PRODUCT_PRICE, {
    onError: error => {
      console.log(error);
    },
    onCompleted: data => {
      const {
        getProduct: { price }
      } = data;

      updateState(prevState => ({ ...prevState, price }));
    }
  });

  const [
    updatePrice,
    { loading: MutationUpdatePriceLoading, error: MutationUpdatePriceError }
  ] = useMutation(MUTATION_PRICE, {
    onError: error => {
      console.log(error);
    },
    onCompleted: () => {
      // alert('Price updated!');
      notification();
    }
  });

  const Toast = () => {
    return (
      <Tween
        // from={{ css: { opacity: 0 } }}
        to={{ css: { opacity: 1 } }}
        // opacity={state.toggleToast}
        duration={5}
      >
        <div
          className="toast"
          role="alert"
          aria-live="assertive"
          aria-atomic="true"
          style={{
            width: '300px',
            position: 'absolute',
            top: 30,
            right: 30
          }}
        >
          <div className="toast-header">
            {/* <img src="..." className="rounded mr-2" alt="..." /> */}
            <svg width="20" height="20">
              <rect
                width="20"
                height="20"
                rx="4"
                ry="4"
                style={{
                  fill: 'rgb(0,122,255)'
                }}
              />
            </svg>
            <strong className="mr-auto" style={{ marginLeft: '0.4rem' }}>
              Toast
            </strong>
            <small className="text-muted">just now</small>
            <button
              type="button"
              className="ml-2 mb-1 close"
              data-dismiss="toast"
              aria-label="Close"
              onClick={() =>
                updateState(prevState => ({ ...prevState, toggleToast: 0 }))
              }
            >
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
          <div className="toast-body">Boom. Your price has been updated.</div>
        </div>
      </Tween>
    );
  };

  const notification = () =>
    updateState(prevState => ({
      ...prevState,
      toggleToast: 1
    }));

  if (error)
    return (
      <div className="container">
        <div className="card shadow rounded p-3 mb-3 col-3">Network Error.</div>
      </div>
    );

  return (
    <div className="container">
      <Toast />
      <div className="row align-items-center">
        <div className="col-12">
          <h1>Dashboard</h1>
        </div>
        <div className="col-12">
          <h4>Item Details</h4>
        </div>
        <div className="card shadow rounded p-3 mb-3 col-3">
          <h6>
            Item Price: {loading ? 'Loading price...' : data.getProduct.price}
          </h6>
          <form onSubmit={() => event.preventDefault()}>
            <div className="form-row">
              <div className="form-group col-md-12">
                <label className="sr-only" htmlFor="inlineFormInputGroup">
                  Item Price
                </label>
                <div className="input-group mb-2">
                  <div className="input-group-prepend">
                    <div className="input-group-text">$</div>
                  </div>
                  <input
                    type="number"
                    className="form-control"
                    id="inlineFormInputGroup"
                    value={loading ? '0.00' : state.price.toFixed(2)}
                    min="0"
                    step="any"
                    onChange={e =>
                      updateState({ price: parseFloat(e.target.value) })
                    }
                  />
                </div>
              </div>
              <button
                type="submit"
                className="btn btn-primary"
                disabled={MutationUpdatePriceLoading ? true : false}
                onClick={() => {
                  updatePrice({
                    variables: { price: parseFloat(state.price.toFixed(2)) },
                    optimisticResponse: {
                      __type: 'Mutation',
                      updatePrice: {
                        __typename: 'Product',
                        id: uuid(),
                        price: parseFloat(state.price.toFixed(2))
                      }
                    },
                    update: (cache, { data: { updatePrice } }) => {
                      const data: any = cache.readQuery({
                        query: QUERY_PRODUCT_PRICE
                      });

                      cache.writeQuery({
                        query: QUERY_PRODUCT_PRICE,
                        data: {
                          ...data,
                          getProduct: { ...data.getProduct, updatePrice }
                        }
                      });
                    }
                  });
                }}
              >
                <span
                  className={`spinner-grow spinner-grow-sm ${
                    MutationUpdatePriceLoading ? null : 'd-none'
                  } `}
                  role="status"
                  aria-hidden="true"
                ></span>
                {MutationUpdatePriceLoading ? ' Loading...' : 'Update'}
              </button>
              {MutationUpdatePriceError ? 'Network error occured.' : ''}
            </div>
          </form>
        </div>
      </div>
      <style jsx>{``}</style>
    </div>
  );
};

export default Dashboard;
