import { useState } from 'react';
import FadeIn from '../../Ui/Animation/FadeIn';
import { Growing } from '../../Ui/Spinner/index';
import { TableData } from '../../Ui/Table';

export interface ProductsTableRowProps {
  id: string;
  title: string;
  price: number;
  inventory: string;
}

const ProductsTableRow: React.SFC<ProductsTableRowProps> = ({
  id,
  title,
  price,
  inventory,
}) => {
  const [imageLoading, updateImageLoading] = useState(true);

  return (
    <FadeIn key={id} isTableRow={true}>
      <TableData>
        <div class="spinner-border" role="status">
          <span class="sr-only">Loading...</span>
        </div>
        <img
          src="https://picsum.photos/50/50"
          onLoad={() => updateImageLoading(false)}
          onError={() => console.log('Image failed to load.')}
          style={{ borderRadius: '8px' }}
        />
        {imageLoading ? 'true' : 'false'}
      </TableData>
      <TableData>{title}</TableData>
      <TableData>${price.toFixed(2)}</TableData>
      <TableData>{inventory} in stock</TableData>
    </FadeIn>
  );
};

export default ProductsTableRow;
