const Product = require('../database/models/Product');

getAllProducts = async (_parent, _args, _context) => {
  try {
    const response = await Product.find();

    if (response.length === 0) throw Error('Collection does not exist');

    return response;
  } catch (err) {
    return err;
  }
};

module.exports = {
  getAllProducts
};
