const Product = require('../database/models/Product');

getAllProducts = async (_parent, _args, _context) => {
  try {
    const response = await Product.find().exec();

    if (response.length === 0) await Product.collection.drop();

    return response;
  } catch (err) {
    return err;
  }
};

module.exports = {
  getAllProducts
};
