import { useEffect } from 'react';
import { TweenMax } from 'gsap';

export interface FadeInProps {
  isTableRow: boolean;
  children: any;
}

const FadeIn: React.SFC<FadeInProps> = ({ isTableRow, children }) => {
  useEffect(() => {
    TweenMax.to(`.animate`, 1, {
      opacity: 1,
    });
  });

  if (isTableRow) return <tr className="animate">{children}</tr>;

  return <div className="animate">{children}</div>;
};

export default FadeIn;
