import { v4 as uuid } from 'uuid';

export const submitPrice = (updater: any, query: any) => {
  updater({
    variables: { price: state.price },
    optimisticResponse: {
      __type: 'Mutation',
      updatePrice: {
        __typename: 'Product',
        id: uuid(),
        price: state.price,
      },
    },
    update: (cache, { data: { updatePrice } }) => {
      const data: any = cache.readQuery({
        query: QUERY_PRODUCT_PRICE,
      });

      cache.writeQuery({
        query: QUERY_PRODUCT_PRICE,
        data: {
          ...data,
          getProduct: { ...data.getProduct, updatePrice },
        },
      });
    },
  });
};
