import { useEffect } from 'react';
import { TweenMax } from 'gsap';

export interface FadeInProps {
  isTableRow?: boolean;
  children: any;
  animateId: string;
}

const FadeIn: React.SFC<FadeInProps> = ({ isTableRow = false, children, animateId }) => {
  useEffect(() => {
    TweenMax.staggerFromTo(`.animate-${animateId}`, 0.5, { y: 10, opacity: 0 }, { y: 0, opacity: 1 }, 0.2);
  });

  if (isTableRow)
    return (
      <tr className={`animate-${animateId}`}>
        {children}
        <style jsx>{`
          .animate-${animateId} {
            opacity: 0;
          }
        `}</style>
      </tr>
    );

  return (
    <div className={`animate-${animateId}`}>
      {children}
      <style jsx>{`
        .animate-${animateId} {
          opacity: 0;
        }
      `}</style>
    </div>
  );
};

export default FadeIn;
