const Config = require('../database/models/GlobalConfig');

getProduct = async (_parent, _args, _context) => {
  try {
    const response = await Config.find();

    if (response.length === 0) throw Error('Collection does not exist.');

    return response[0];
  } catch (err) {
    return err;
  }
};

module.exports = {
  getProduct
};
