const gql = require('graphql-tag');

const QUERY_GLOBAL_CONFIG = gql`
  query {
    getGlobalConfig @client {
      businessName
      fullName
    }
  }
`;

getGlobalConfig = (_root, variables, { cache, getCacheKey }) => {
  const data = cache.readQuery({ query: QUERY_GLOBAL_CONFIG });

  if (!data) return;
  console.log(_root);
  console.log(variables);
  // return cartItems.includes(launch.id);
  // return { businessName: '', __typename: 'Config' };
};

module.exports = {
  getGlobalConfig,
};
