import { ApolloServer } from 'apollo-server-micro';
import '../../server/database/config';

//Resolvers
import Query from '../../server/resolvers/Query';
// import Mutation from '../../server/resolvers/Mutation';

// Schema
import { query } from '../../server/schema/query';
// import { mutation } from '../../server/schema/mutation';
import { game } from '../../server/schema/game';

const resolvers = {
  Query,
  Mutation
};

const apolloServer = new ApolloServer({
  typeDefs: [query, game],
  resolvers
});

const handler = apolloServer.createHandler({ path: '/api/graphql' });

export const config = {
  api: {
    bodyParser: false
  }
};

export default handler;
