const Game = require('../database/models/Game');
const Product = require('../database/models/Product');

getAllGames = async (_parent, _args, _context) => {
  try {
    const response = await Game.find().exec();

    return response;
  } catch (err) {
    return err;
  }
};

getAllProducts = (_parent, _args, _context) => {
  try {
    const response = Product.find().exec();

    return response;
  } catch (err) {
    return err;
  }
};

module.exports = {
  getAllGames,
  getAllProducts
};
