import React, { useEffect, useState } from 'react';

export interface ToastProps {
  toggleToast: number;
  updater: any;
}

export interface ToastState {
  second: number;
}

const Toast: React.SFC<ToastProps> = ({ toggleToast, updater }) => {
  const [seconds, updateSecond] = useState<ToastState>({ second: 3 });

  useEffect(() => {
    console.log(seconds);

    if (toggleToast === 1) {
      setInterval(() => {
        if (seconds.second > 0) {
          //   console.log(`Toast will close in ${seconds.second} seconds...`);
          console.log(seconds);
          updateSecond(({ second }) => ({ second: second - 1 }));
          //   console.log('Still Counting Down!');
        }

        if (seconds.second === 0) {
          return;
        }

        // updateState(prevState => ({ ...prevState, toggleToast: 0 }));
      }, 1000);
    }
  }, [toggleToast]);

  return (
    <div
      className="toast"
      role="alert"
      aria-live="assertive"
      aria-atomic="true"
      style={{
        width: '300px',
        position: 'absolute',
        top: 30,
        right: 30,
        opacity: toggleToast
      }}
    >
      <div className="toast-header">
        {/* <img src="..." className="rounded mr-2" alt="..." /> */}
        <svg width="20" height="20">
          <rect
            width="20"
            height="20"
            rx="4"
            ry="4"
            style={{
              fill: 'rgb(0,122,255)'
            }}
          />
        </svg>
        <strong className="mr-auto" style={{ marginLeft: '0.4rem' }}>
          Toast
        </strong>
        <small className="text-muted">just now</small>
        <button
          type="button"
          className="ml-2 mb-1 close"
          data-dismiss="toast"
          aria-label="Close"
          onClick={() =>
            updater(prevState => ({ ...prevState, toggleToast: 0 }))
          }
        >
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="toast-body">Boom. Your price has been updated.</div>
    </div>
  );
};

export default Toast;
