import React from 'react';
import { useRouter } from 'next/router';
import { useMutation } from '@apollo/react-hooks';
import gql from 'graphql-tag';

export interface SetupProps {}

const RUN_SETUP = gql`
  mutation runSetup($success: Boolean) {
    runSetup(success: $success) {
      success
    }
  }
`;

const Setup: React.FC<SetupProps> = () => {
  const router = useRouter();
  console.log(router);

  const [runSetup, { loading: runSetupLoading }]: any = useMutation(RUN_SETUP, {
    onCompleted: () => {
      alert('Okay your ready to rock!');
    }
  });

  const runFullSetup = (success: boolean) => {
    runSetup({ variables: { success } });
  };

  const loadSpinner = () => {
    return (
      <div
        style={{ marginLeft: '1rem' }}
        className="spinner-border spinner-border-sm text-primary"
        role="status"
      >
        <span className="sr-only">Loading...</span>
      </div>
    );
  };

  return (
    <div className="container">
      <h1>SetUp Page</h1>
      <p>All right your ready to run your website setup!</p>
      <button
        type="button"
        className="btn btn-primary"
        onClick={() => runFullSetup(true)}
      >
        Run SetUp
      </button>
      {runSetupLoading ? loadSpinner() : ''}
    </div>
  );
};

export default Setup;
