import { useEffect } from 'react';
import { TweenMax } from 'gsap';

export interface FadeInProps {
  isTableRow?: boolean;
  children: any;
  myId: any;
}

const FadeIn: React.SFC<FadeInProps> = ({ isTableRow = false, children, myId }) => {
  useEffect(() => {
    TweenMax.staggerFromTo(`.animate-${myId}`, 0.5, { y: 10, opacity: 0 }, { y: 0, opacity: 1 }, 0.2);
  }, []);

  if (isTableRow) return <tr className={`animate-${myId}`}>{children}</tr>;

  return <div className="animate">{children}</div>;
};

export default FadeIn;
