import React from 'react';
import { useState } from 'react';
import { useMutation } from '@apollo/react-hooks';
import { MUTATION_PRICE } from '../graphql';

export interface DashboardProps {}

export interface DashboardState {
  price: number;
}

const Dashboard: React.FC<DashboardProps> = () => {
  const [state, updateState] = useState<DashboardState>({
    price: (7.0 * 100 + 0.0 * 100) / 100
  });

  const [updatePrice, { loading: MutationUpdatePrice, error }] = useMutation(
    MUTATION_PRICE,
    {
      onError: error => {
        console.log(error);
      },
      onCompleted: () => {
        alert('Price mutation completed!');
      }
    }
  );

  return (
    <div className="container">
      <div className="row align-items-center">
        <div className="col-12">
          <h1>Dashboard</h1>
        </div>
        <div className="col-12">
          <h4>Item Details</h4>
        </div>
        <div className="card shadow rounded p-3 mb-3 col-3">
          <h6>Item Price: {state.price.toFixed(2)}</h6>
          <form onSubmit={() => event.preventDefault()}>
            <div className="form-row">
              <div className="form-group col-md-12">
                <label className="sr-only" htmlFor="inlineFormInputGroup">
                  Username
                </label>
                <div className="input-group mb-2">
                  <div className="input-group-prepend">
                    <div className="input-group-text">$</div>
                  </div>
                  <input
                    type="number"
                    className="form-control"
                    id="inlineFormInputGroup"
                    value={state.price.toFixed(2)}
                    min="0"
                    step="any"
                    onChange={e =>
                      updateState({ price: parseFloat(e.target.value) })
                    }
                  />
                </div>
              </div>
              <button
                type="submit"
                className="btn btn-primary"
                disabled={false}
                onClick={() => {
                  updatePrice({
                    variables: { price: parseFloat(state.price.toFixed(2)) }
                  });
                }}
              >
                <span
                  className={`spinner-grow spinner-grow-sm ${
                    MutationUpdatePrice ? null : 'd-none'
                  } `}
                  role="status"
                  aria-hidden="true"
                ></span>
                {MutationUpdatePrice ? '...Loading' : 'Update'}
              </button>
              <button className="btn btn-primary" type="button" disabled>
                <span
                  className="spinner-grow spinner-grow-sm"
                  role="status"
                  aria-hidden="true"
                ></span>
                Loading...
              </button>
            </div>
          </form>
        </div>
      </div>
      <style jsx>{``}</style>
    </div>
  );
};

export default Dashboard;
