import React from 'react';

export interface ToastProps {}

const Toast: React.SFC<ToastProps> = () => {
  return (
    <div
      className="toast"
      role="alert"
      aria-live="assertive"
      aria-atomic="true"
      style={{
        width: '300px',
        position: 'absolute',
        top: 30,
        right: 30,
        opacity: state.toggleToast
      }}
    >
      <div className="toast-header">
        {/* <img src="..." className="rounded mr-2" alt="..." /> */}
        <svg width="20" height="20">
          <rect
            width="20"
            height="20"
            rx="4"
            ry="4"
            style={{
              fill: 'rgb(0,122,255)'
            }}
          />
        </svg>
        <strong className="mr-auto" style={{ marginLeft: '0.4rem' }}>
          Toast
        </strong>
        <small className="text-muted">just now</small>
        <button
          type="button"
          className="ml-2 mb-1 close"
          data-dismiss="toast"
          aria-label="Close"
          onClick={() =>
            updateState(prevState => ({ ...prevState, toggleToast: 0 }))
          }
        >
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="toast-body">Boom. Your price has been updated.</div>
    </div>
  );
};

export default Toast;
