const Game = require('../database/models/Game');

getAllGames = async (_parent, _args, _context) => {
  try {
    const response = await Game.find().exec();

    return response;
  } catch (err) {
    return err;
  }
};

getAllProducts = () => {
  return [{ price: 4 }];
};

module.exports = {
  getAllGames,
  getAllProducts
};
