import React, { useEffect, useState, useRef } from 'react';

export interface ToastProps {
  id: number;
  title: string;
  text: string;
  removeToast: any;
}

export interface ToastState {
  second: number;
  otherSecond: number;
}

const Toast: React.SFC<ToastProps> = ({ id, title, text, removeToast }) => {
  const [state, updateState] = useState<ToastState>({
    second: 100,
    otherSecond: 0,
  });

  useEffect(() => {
    console.log(state.otherSecond);
    if (state.second === 0) {
      removeToast(id);
    }
  });

  const useInterval = (callback, delay) => {
    const savedCallback: any = useRef();

    useEffect(() => {
      savedCallback.current = callback;
    }, [callback]);

    useEffect(() => {
      let id = setInterval(tick, delay);

      function tick() {
        savedCallback.current();
      }

      if (state.second === 0) {
        clearInterval(id);
      }

      if (delay !== null) {
        return () => clearInterval(id);
      }
    });
  };

  useInterval(() => {
    updateState({
      second: state.second - 1,
      otherSecond: parseFloat(state.otherSecond.toFixed(2)) + 10 / 100,
    });
  }, 100);

  return (
    <div
      className="toast"
      role="alert"
      aria-live="assertive"
      aria-atomic="true"
      style={{
        marginBottom: '1rem',
        opacity: 1,
      }}
    >
      <div className="toast-header">
        <svg width="20" height="20">
          <rect
            width="20"
            height="20"
            rx="4"
            ry="4"
            style={{
              fill: 'rgb(0,122,255)',
            }}
          />
        </svg>
        <strong className="mr-auto" style={{ marginLeft: '0.4rem' }}>
          {title}
        </strong>
        <small className="text-muted">just now {state.otherSecond}</small>
        <button
          type="button"
          className="ml-2 mb-1 close"
          data-dismiss="toast"
          aria-label="Close"
          onClick={() => removeToast(id)}
        >
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="toast-body">{text}</div>
      <div className="progress-bar" style={{ width: state.second + '%' }} />
      <style jsx>
        {`
          .progress-bar {
            position: absolute;
            bottom: 0;
            left: 0;
            height: 1px;
            background-color: #007bff;
            transition: width 0.1s ease;
          }
        `}
      </style>
    </div>
  );
};

export default Toast;
