import React, { useState } from 'react';
import Link from 'next/link';
import {
  FormGroup,
  InputText,
  InputPrice,
  InputNumber,
} from '../../components/Form';
import { MUTATION_ADD_PRODUCT } from '../../graphql';
import { Button } from '../../components/Button';
import { Growing } from '../../components/Spinner';

export interface ProductState {
  title: string;
  price: number;
  inventory: number;
}

export interface ProductProps {}

const Product: React.SFC<ProductProps> = () => {
  const [state, updateState]: any = useState<ProductState>({
    title: '',
    price: 0,
    inventory: 0,
  });

  // const [
  //   updatePrice,
  //   { loading: MutationUpdatePriceLoading, error: MutationUpdatePriceError },
  // ] = useMutation(MUTATION_PRICE, {
  //   onError: (error) => {
  //     console.log(error);
  //   },
  //   onCompleted: () => {
  //     addNotification(
  //       'Notification',
  //       // `Your price has been updated to $${state.price.toFixed(2)}`,
  //       `Your price has been updated to $0`,
  //       'info',
  //       notifications,
  //       updateNotifications
  //     );
  //   },
  // });

  const onChange = (e) => {
    const { name, value, type } = e.target;

    if (type === 'number') {
      if (value < 0) return;

      return updateState((prevState) => ({
        ...prevState,
        [name]: parseFloat(value),
      }));
    }

    updateState((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const MutationUpdatePriceLoading = false;
  const MutationUpdatePriceError = false;

  return (
    <div className="col pt-5">
      <div className="container">
        <Link href="/dashboard/products">
          <div
            style={{
              cursor: 'pointer',
              marginBottom: '1rem',
            }}
          >
            <svg
              className="bi bi-arrow-left-short"
              width="25px"
              height="25px"
              viewBox="0 0 16 16"
              fill="currentColor"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                fillRule="evenodd"
                d="M7.854 4.646a.5.5 0 010 .708L5.207 8l2.647 2.646a.5.5 0 01-.708.708l-3-3a.5.5 0 010-.708l3-3a.5.5 0 01.708 0z"
                clipRule="evenodd"
              />
              <path
                fillRule="evenodd"
                d="M4.5 8a.5.5 0 01.5-.5h6.5a.5.5 0 010 1H5a.5.5 0 01-.5-.5z"
                clipRule="evenodd"
              />
            </svg>
            Products
          </div>
        </Link>
        <h3 className="mb-5">Add Product</h3>
        <div className="form-row">
          <InputText
            onChange={onChange}
            labelTitle="Title"
            name="title"
            placeholderTitle="Short sleeve t-shirt"
          />
        </div>
        <div className="form-row">
          <FormGroup>
            <InputPrice
              onChange={onChange}
              labelTitle="Price"
              value={state.price}
            />
          </FormGroup>
          <FormGroup>
            <InputNumber
              onChange={onChange}
              labelTitle="Inventory"
              name="inventory"
              value={state.inventory}
            />
          </FormGroup>
        </div>
        <Button mutationLoading={false} onClick={() => alert('Hello')}>
          <Growing visible={MutationUpdatePriceLoading} />
          {MutationUpdatePriceLoading ? ' Loading...' : 'Save'}
          {MutationUpdatePriceError ? 'Network error occured.' : ''}
        </Button>
      </div>
    </div>
  );
};

export default Product;
