import React from 'react';
import { useState } from 'react';
import { useQuery, useMutation } from '@apollo/react-hooks';
import { QUERY_PRODUCT_PRICE, MUTATION_PRICE } from '../graphql';
import { addNotification } from '../utils/toast';
import { submitPrice } from '../utils/price';
import ToastContainer from '../components/ToastContainer';
import { Card, CardBody, CardTitle } from '../components/Card';
import { FormGroup, InputPrice } from '../components/Form';

export interface DashboardState {
  price: number;
}

interface ProductInventoryData {
  getProduct: ProductInventory;
}

interface ProductInventory {
  price: number;
}

const Dashboard: React.FC = () => {
  // State
  const [state, updateState] = useState<DashboardState>({
    price: 0,
  });
  const [notifications, updateNotifications] = useState([]);

  // Querys
  const { loading, error, data } = useQuery<ProductInventoryData>(
    QUERY_PRODUCT_PRICE,
    {
      onError: (error) => {
        console.log(error);
      },
      onCompleted: (data) => {
        const {
          getProduct: { price },
        } = data;

        updateState((prevState) => ({ ...prevState, price }));
      },
    }
  );

  // Mutations
  const [
    updatePrice,
    { loading: MutationUpdatePriceLoading, error: MutationUpdatePriceError },
  ] = useMutation(MUTATION_PRICE, {
    onError: (error) => {
      console.log(error);
    },
    onCompleted: () => {
      addNotification(
        'Notification',
        `Your price has been updated to $${state.price.toFixed(2)}`,
        'info',
        notifications,
        updateNotifications
      );
    },
  });

  // Local Methods
  const removeNotification = (id: number) => {
    const data = notifications;
    const toasts = data.filter((item): any => item.id !== id);

    updateNotifications(toasts);
  };

  const onChangeMe = (e) => {
    const price = parseFloat(e.target.value);

    updateState({ price });
  };

  if (loading) {
    return (
      <div className="container-fluid">
        <div className="spinner-grow" role="status">
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    );
  }

  if (error)
    return (
      <div className="container">
        <div className="card shadow rounded p-3 mb-3 col-3">Network Error.</div>
      </div>
    );

  const {
    getProduct: { price },
  } = data;

  return (
    <div className="container">
      <ToastContainer
        allToasts={notifications}
        removeNotification={removeNotification}
      />
      <div className="row align-items-center">
        <div className="col-12">
          <h1>Dashboard</h1>
        </div>
        <div className="col-12">
          <h4>Item Details</h4>
        </div>
        <div className="p-3 mb-3 col-3">
          <Card>
            <CardBody>
              <CardTitle>Item Price: {price.toFixed(2)}</CardTitle>
              <form onSubmit={() => event.preventDefault()}>
                <FormGroup>
                  <InputPrice
                    onChange={onChangeMe}
                    labelTitle="Item Price"
                    price={state.price.toFixed(2)}
                  />
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={MutationUpdatePriceLoading ? true : false}
                    onClick={() =>
                      submitPrice(updatePrice, QUERY_PRODUCT_PRICE, state.price)
                    }
                  >
                    <span
                      className={`spinner-grow spinner-grow-sm ${
                        MutationUpdatePriceLoading ? null : 'd-none'
                      } `}
                      role="status"
                      aria-hidden="true"
                    ></span>
                    {MutationUpdatePriceLoading ? ' Loading...' : 'Update'}
                  </button>
                  {MutationUpdatePriceError ? 'Network error occured.' : ''}
                </FormGroup>
              </form>
            </CardBody>
          </Card>
        </div>
      </div>
      <style jsx>{``}</style>
    </div>
  );
};

export default Dashboard;
