import React from 'react';
import { useState } from 'react';

export interface DashboardProps {}

export interface DashboardState {
  price: number;
}

const Dashboard: React.FC<DashboardProps> = () => {
  const numUSD = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2,
    currencyDisplay: 'symbol'
  });
  const [state, updateState] = useState<DashboardState>({
    price: (7.0 * 100 + 0.0 * 100) / 100
  });

  return (
    <div className="container">
      <div className="row align-items-center">
        <div className="col-12">
          <h1>Dashboard</h1>
        </div>
        <div className="col-3">
          <h4>Item Details</h4>
          <h6>Price: {state.price.toFixed(2)}</h6>
          <form onSubmit={() => event.preventDefault()}>
            <div className="form-row">
              <div className="form-group col-md-12">
                <label className="sr-only" htmlFor="inlineFormInputGroup">
                  Username
                </label>
                <div className="input-group mb-2">
                  <div className="input-group-prepend">
                    <div className="input-group-text">$</div>
                  </div>
                  <input
                    type="number"
                    className="form-control"
                    id="inlineFormInputGroup"
                    value={state.price.toFixed(2)}
                    min="0"
                    step="any"
                    onChange={e =>
                      updateState({ price: parseFloat(e.target.value) })
                    }
                  />
                </div>
              </div>
              <button
                type="submit"
                className="btn btn-primary"
                onClick={() => alert(state.price)}
              >
                Sign in
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
