import { useState, useEffect } from 'react';

const HomePage = () => {
  const [height, updateHeight] = useState(0);

  useEffect(() => {
    console.log('Component did mount!');
    updateHeight(window.innerHeight);
  });

  return (
    <div className="container">
      <div className="row align-items-center">
        <div className="col-sm order-2 order-md-1">
          <h1>Purell Santizer</h1>
          <p>
            Keep your loved ones clean. <br /> In stock and ready to ship.
          </p>
          <div className="button_container">
            <button type="button" className="btn btn-primary">
              Buy Now
            </button>
          </div>
        </div>
        <div className="col-sm order-1 order-md-2">
          <img src="purell-sanitizer-01.jpg" alt="purell-sanitizer" />
        </div>
      </div>
      <style jsx>{`
        .container,
        .row {
          height: ${height}px;
        }
        h1 {
          font-size: 48px;
          font-weight: 500;
          text-align: center;
          line-height: 1;
        }
        p {
          font-size: 20px;
          font-weight: 400;
          text-align: center;
          margin-bottom: 2rem;
        }
        .button_container {
          text-align: center;
        }
        button {
          width: 322px;
          font-size: 27px;
          text-transform: uppercase;
          padding: 1.7rem 0.75rem;
          border-radius: 50px;
        }
        img {
          width: 100%;
        }

        @media (min-width: 768px) {
          h1 {
            font-size: 150px;
            text-align: left;
          }
          p {
            font-size: 34px;
            text-align: left;
          }
          .button_container {
            text-align: left;
          }
        }
      `}</style>
    </div>
  );
};

export default HomePage;
