// import axios from 'axios';
import { useState, useEffect } from 'react';
import { QUERY_GLOBAL_CONFIG } from '../../graphql';
import { execute, makePromise } from 'apollo-link';
import { HttpLink } from 'apollo-link-http';

const uri = 'http://localhost:3000/api/graphql';
const link = new HttpLink({ uri });

const operation = {
  query: QUERY_GLOBAL_CONFIG,
};

export default function () {
  const [config, updateConfig] = useState({});

  useEffect(() => {
    console.log('Hello');
    try {
      makePromise(execute(link, operation))
        .then((data) =>
          console.log(`received data ${JSON.stringify(data, null, 2)}`)
        )
        .catch((error) => console.log(`received error ${error}`));
    } catch (error) {}
    // try {
    //   const todoData = await API.graphql(graphqlOperation(query));
    //   updateTodos(todoData.data.listTodos.items);
    // } catch (err) {
    //   console.log('error: ', err);
    // }
  }, []);

  return config;
}
