import React, { useEffect, useState, useRef } from 'react';

export interface ToastProps {
  id: number;
  title: string;
  text: string;
  removeToast: any;
}

export interface ToastState {
  second: number;
}

const Toast: React.SFC<ToastProps> = ({ id, title, text, removeToast }) => {
  const [state, updateState] = useState<ToastState>({ second: 3 });

  useEffect(() => {
    if (state.second === 0) {
      console.log('I run when second is 0!');
      removeToast(id);
    }
  });

  const useInterval = (callback, delay) => {
    const savedCallback: any = useRef();

    useEffect(() => {
      savedCallback.current = callback;
    }, [callback]);

    useEffect(() => {
      let id = setInterval(tick, delay);

      function tick() {
        savedCallback.current();
      }

      if (state.second === 0) {
        clearInterval(id);
      }

      if (delay !== null) {
        return () => clearInterval(id);
      }
    });
  };

  useInterval(() => {
    updateState({
      second: state.second - 1
    });
  }, 1000);

  return (
    <div
      className="toast"
      role="alert"
      aria-live="assertive"
      aria-atomic="true"
      style={{
        width: '300px',
        position: 'absolute',
        top: 30,
        right: 30
      }}
    >
      <div className="toast-header">
        {/* <img src="..." className="rounded mr-2" alt="..." /> */}
        <svg width="20" height="20">
          <rect
            width="20"
            height="20"
            rx="4"
            ry="4"
            style={{
              fill: 'rgb(0,122,255)'
            }}
          />
        </svg>
        <strong className="mr-auto" style={{ marginLeft: '0.4rem' }}>
          Toast
        </strong>
        <small className="text-muted">just now</small>
        <button
          type="button"
          className="ml-2 mb-1 close"
          data-dismiss="toast"
          aria-label="Close"
          onClick={() => console.log('Hello')}
        >
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="toast-body">
        Boom. Your price has been updated. {state.second}
      </div>
      <div className="progress-bar" />
      <style jsx>
        {`
          .progress-bar {
            position: absolute;
            bottom: 0;
            left: 0;
            width: ${((seconds.second * 100) / 3).toString() + '%'};
            height: 1px;
            background-color: #007bff;
            -webkit-transition: all 0.5s linear;
            -moz-transition: all 0.5s linear;
            -ms-transition: all 0.5s linear;
            -o-transition: all 0.5s linear;
            transition: all 0.5s linear;
          }
        `}
      </style>
    </div>
  );
};

export default Toast;
