import { useEffect } from 'react';
import { TweenMax } from 'gsap';
import { v4 as uuid } from 'uuid';

export interface FadeInProps {
  isTableRow?: boolean;
  children: any;
}

const FadeIn: React.SFC<FadeInProps> = ({ isTableRow = false, children }) => {
  const id = uuid();

  useEffect(() => {
    TweenMax.staggerFromTo(`.animate-${id}`, 0.5, { y: 10, opacity: 0 }, { y: 0, opacity: 1 }, 0.2);
  }, []);

  if (isTableRow) return <tr className={`animate-${id}`}>{children}</tr>;

  return <div className="animate">{children}</div>;
};

export default FadeIn;
