const { Game } = require('../database/models/game');

getAllGames = async (_parent, _args, _context) => {
  try {
    const response = await Game.find();

    return response;
  } catch (err) {
    return err;
  }

  // return [{ name: 'Final Fantasy 7 Remake' }];
};

module.exports = {
  getAllGames
};
