// import axios from 'axios';
import { useState, useEffect } from 'react';
import { QUERY_GLOBAL_CONFIG } from '../../graphql';
import { execute, makePromise } from 'apollo-link';
import { HttpLink } from 'apollo-link-http';

const uri = 'http://localhost:3000/api/graphql';
const link = new HttpLink({ uri });

const operation = {
  query: QUERY_GLOBAL_CONFIG,
};

export default function () {
  const [config, updateConfig] = useState({
    data: {},
    loading: false,
  });

  async function myFunc() {
    try {
      updateConfig((prevState) => ({ ...prevState, loading: true }));
      const response = await makePromise(execute(link, operation));
      updateConfig({ data: response, loading: false });
    } catch (error) {
      console.log(`received error ${error}`);
    }
  }

  useEffect(() => {
    myFunc();
  }, []);

  return config;
}
