import { useEffect } from 'react';
import { TweenMax } from 'gsap';

export interface FadeInProps {
  children: any;
}

const FadeIn: React.SFC<FadeInProps> = ({ children }) => {
  useEffect(() => {
    TweenMax.staggerFromTo(`.animate`, 1, { opacity: 1 }, { opacity: 0 }, 0.2);
  });

  return (
    <div className="animate">
      {children}
      <style jsx>
        {`
          .animate {
            opacity: 0;
          }
        `}
      </style>
    </div>
  );
};

export default FadeIn;
