import React, { useEffect, useState, useRef } from 'react';

export interface ToastProps {
  toggleToast: number;
  updater: any;
}

export interface ToastState {
  second: number;
}

const Toast: React.SFC<ToastProps> = ({ toggleToast, updater }) => {
  const [seconds, updateSecond] = useState<ToastState>({ second: 3 });

  const useInterval = (callback, delay) => {
    const savedCallback: any = useRef();

    useEffect(() => {
      savedCallback.current = callback;
    }, [callback]);

    useEffect(() => {
      function tick() {
        savedCallback.current();
      }

      if (delay !== null) {
        let id = setInterval(tick, delay);
        return () => clearInterval(id);
      }
    });
  };

  useInterval(() => {
    const { second } = seconds;

    if (second === 0)
      return updater(prevState => ({ ...prevState, toggleToast: 0 }));

    updateSecond({ second: second - 1 });
  }, 1000);

  return (
    <div
      className="toast"
      role="alert"
      aria-live="assertive"
      aria-atomic="true"
      style={{
        width: '300px',
        position: 'absolute',
        top: 30,
        right: 30,
        opacity: toggleToast
      }}
    >
      <div className="toast-header">
        {/* <img src="..." className="rounded mr-2" alt="..." /> */}
        <svg width="20" height="20">
          <rect
            width="20"
            height="20"
            rx="4"
            ry="4"
            style={{
              fill: 'rgb(0,122,255)'
            }}
          />
        </svg>
        <strong className="mr-auto" style={{ marginLeft: '0.4rem' }}>
          Toast
        </strong>
        <small className="text-muted">just now</small>
        <button
          type="button"
          className="ml-2 mb-1 close"
          data-dismiss="toast"
          aria-label="Close"
          onClick={() =>
            updater(prevState => ({ ...prevState, toggleToast: 0 }))
          }
        >
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="toast-body">
        Boom. Your price has been updated. {seconds.second}
      </div>
      <div className="progress-bar" />
      <style jsx>
        {`
          .progress-bar {
            position: absolute;
            bottom: 0;
            left: 0;
            width: ${((seconds.second * 100) / 3).toString() + '%'};
            height: 1px;
            background-color: #007bff;
          }
        `}
      </style>
    </div>
  );
};

export default Toast;
