import { useState, useEffect } from 'react';
import { Growing } from '../Spinner';

export interface ImageProps {
  imageUrl: string;
}

const Image: React.SFC<ImageProps> = ({ imageUrl }) => {
  const [imageLoading, updateImageLoading] = useState(true);

  return (
    <>
      {imageLoading ? (
        <div style={{ width: 50, height: 50, backgroundColor: '#ccc' }}></div>
      ) : (
        <div style={{ width: 50, height: 50, backgroundColor: 'red' }}></div>
      )}
      {/* {imageLoading ? (
