const gql = require('graphql-tag');

const QUERY_GLOBAL_CONFIG = gql`
  query {
    getGlobalConfig {
      businessName
      fullName
    }
  }
`;

getGlobalConfig = (_, args, { cache }) => {
  const { config } = cache.readQuery({ query: QUERY_GLOBAL_CONFIG });
};

module.exports = {
  getGlobalConfig,
};
