import * as React from 'react';

export interface ButtonProps {
  submitPrice: any;
  updatePrice: any;
  query: any;
  state: any;
  mutateLoading: any;
}

const Button: React.SFC<ButtonProps> = ({
  children,
  submitPrice,
  updatePrice,
  query: QUERY_PRODUCT_PRICE,
  state,
  mutateLoading,
}) => {
  return (
    <button
      type="submit"
      className="btn btn-primary"
      disabled={mutateLoading ? true : false}
      onClick={() => submitPrice(updatePrice, QUERY_PRODUCT_PRICE, state.price)}
    >
      {children}
    </button>
  );
};

export default Button;
