import React, { useState } from 'react';
import Link from 'next/link';
import { FormGroup, InputText, InputPrice } from '../../components/Form';

export interface ProductState {
  title: string;
  price: number;
}

export interface ProductProps {}

const Product: React.SFC<ProductProps> = () => {
  const [state, updateState]: any = useState<ProductState>({
    title: '',
    price: 0,
  });

  const onChange = (e) => {
    const { name, value } = e.target;

    console.log(name);

    if (name === 'price')
      updateState((prevState) => ({ ...prevState, [name]: toCurrency(value) }));

    updateState((prevState) => ({ ...prevState, [name]: value }));
  };

  const toCurrency = (number) => {
    const formatter = new Intl.NumberFormat('sv-SE', {
      style: 'currency',
      currency: 'USD',
    });

    return formatter.format(number);
  };

  return (
    <div className="col pt-5">
      <div className="container">
        <Link href="/dashboard/products">
          <div style={{ cursor: 'pointer', marginBottom: '1rem' }}>
            <svg
              className="bi bi-arrow-left-short"
              width="25px"
              height="25px"
              viewBox="0 0 16 16"
              fill="currentColor"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                fillRule="evenodd"
                d="M7.854 4.646a.5.5 0 010 .708L5.207 8l2.647 2.646a.5.5 0 01-.708.708l-3-3a.5.5 0 010-.708l3-3a.5.5 0 01.708 0z"
                clipRule="evenodd"
              />
              <path
                fillRule="evenodd"
                d="M4.5 8a.5.5 0 01.5-.5h6.5a.5.5 0 010 1H5a.5.5 0 01-.5-.5z"
                clipRule="evenodd"
              />
            </svg>
            Products
          </div>
        </Link>
        <h3 className="mb-0">Add Product - {state.title}</h3>
        <FormGroup>
          <InputText
            onChange={onChange}
            labelTitle="Title"
            name="title"
            placeholderTitle="Short sleeve t-shirt"
          />
          <InputPrice
            onChange={onChange}
            labelTitle="Price"
            value={state.price.toFixed(2)}
          />
        </FormGroup>
      </div>
    </div>
  );
};

export default Product;
